import React from 'react';
import { Link } from 'react-router-dom';

const Pagination = ({ cocktailsPerPage, totalCocktails, paginate }) => {
  const pageNumbers = []; 

  for(let i=1; i <= Math.ceil(totalCocktails / cocktailsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <ul className="tpagination"> 
        {pageNumbers.map(number => (
          <li key={number} className="tpage-item">
            <Link onClick={() => paginate(number)} href='!#' className="tpage-link">
              {number}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  )
}

export default Pagination;
